import React, { useState } from 'react';

export const Search = ({ products }) => {
    const [searchTerm, setSearchTerm] = useState(null);
    const [isExactMatch, setIsExactMatch] = useState(false);
    const filteredProducts = products.filter(product => {
        if (isExactMatch) {
            return product.name === searchTerm;
        } else {
            return product.name.toLowerCase().includes(searchTerm.toLowerCase());
        }
    });

    return (
        <div>
            <input type="text" value={searchTerm} onChange={e => setSearchTerm(e.target.value)} placeholder="Поиск по наименованию товара" />
            <input type="checkbox" checked={isExactMatch} onChange={e => setIsExactMatch(e.target.checked)} /> Точное совпадение
            {filteredProducts.map(product => (
                <div key={product.id}>
                    <h2>{product.name}</h2>
                    <p>Количество: {product.quantity}</p>
                    <p>Стоимость: {product.cost}</p>
                    <p>Стоимость со скидкой: {product.discount ? (product.cost * (1 - product.discount / 100)).toFixed(2) : product.cost}</p>
                    <p>Описание: {product.description}</p>
                </div>
            ))}
        </div>
    );
};
