import React, { useState } from 'react';

// Компонент ProductCard
const ProductCard = ({ product }) => {
  return (
      <div>
        <h2>{product.name}</h2>
        <img src={product.image} alt={product.name} style={{ width: '50px' }} />
        <p>Стоимость: {product.price}</p>
        <p>Скидка: {product.discount}%</p>
        <p>Количество: {product.quantity}</p>
        <p>Описание: {product.description}</p>
        <p>Новинка: {product.isNew ? 'Да' : 'Нет'}</p>
      </div>
  );
};

// Главный компонент
const App = () => {
  const [products, setProducts] = useState([
    {
      name: 'Товар 1',
      price: 100,
      quantity: 10,
      image: 'url_изображения_1',
      description: 'Описание товара 1',
      isNew: true,
      discount: 10
    },
    {
      name: 'Товар 2',
      price: 200,
      quantity: 20,
      image: 'url_изображения_2',
      description: 'Описание товара 2',
      isNew: false,
      discount: 20
    },
    {
      name: 'Товар 3',
      price: 300,
      quantity: 30,
      image: 'url_изображения_3',
      description: 'Описание товара 3',
      isNew: true,
      discount: 0
    },
    // Добавьте здесь больше продуктов по аналогии
  ]);

  const sortProducts = (field) => {
    const sortedProducts = [...products].sort((a, b) => a[field] > b[field] ? 1 : -1);
    setProducts(sortedProducts);
  };

  return (
      <div>
        <button onClick={() => sortProducts('name')}>Сортировать по наименованию</button>
        <button onClick={() => sortProducts('price')}>Сортировать по стоимости</button>
        <button onClick={() => sortProducts('quantity')}>Сортировать по количеству</button>
        <button onClick={() => sortProducts('discount')}>Сортировать по скидке</button>
        {products.map((product, index) => (
            <ProductCard key={index} product={product} />
        ))}
      </div>
  );
};

export default App;
