import React, { useState } from 'react';
import './catalog.css'
// Товар
const Product = ({ product }) => (
    <div id="product">
        <h2>{product.name}</h2>
        <img src={product.image} alt={product.name} width="100" height="100" />
        <p>{product.description}</p>
        <p>Стоимость: {product.discount ? (product.cost * (1 - product.discount / 100)).toFixed(2) : product.cost} (Полная стоимость: {product.cost})</p>
        <p>Количество: {product.quantity}</p>
        <p>Новинка: {product.isNew ? 'Да' : 'Нет'}</p>
        <p>Скидка: {product.discount}%</p>
    </div>
);

// Сортировка таблицы
const Sort = ({ products, sortField, setSortField }) => {
    const sortedProducts = [...products].sort((a, b) => a[sortField] > b[sortField] ? 1 : -1);
    return (
        <div>
            {sortedProducts.map(product => <Product key={product.id} product={product} />)}
            <button onClick={() => setSortField('name')}>Сортировать по наименованию</button>
            <button onClick={() => setSortField('cost')}>Сортировать по стоимости</button>
            <button onClick={() => setSortField('quantity')}>Сортировать по количеству</button>
            <button onClick={() => setSortField('discount')}>Сортировать по скидке</button>
        </div>
    );
};

// Каталог
const Catalog = ({ products }) => {
    const [sortField, setSortField] = useState('name');
    return <Sort products={products} sortField={sortField} setSortField={setSortField} />;
};

export default Catalog;
